mapper.select:
{  
	CALL qlistQna( 
		#{startNum}, #{endNum},
		#{ref_cursor , mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=qnaMap}
	)  
}

{  
	CALL qgetQna( 
		#{qseq},
		#{ref_cursor , mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=qnaMap},
		#{ref_cursor1 , mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=replyMap}
	)	  
}

{  
	CALL qmylistQna( 
		#{id}, #{startNum}, #{endNum},
		#{ref_cursor , mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=qnaMap}
	)  
}

{  
	CALL qlistQnaN( 
		#{startNum}, #{endNum},
		#{ref_cursor , mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=qnaMap}
	)  
}

{
	CALL replycnt(
		select count(*) from tp_qreply where qnanum=#{qseq}
	)
}

{  
